#define N 100010#include<stdio.h>#include<string.h>int num[N], t1 = -1, t2 = -1, ls;char op[N], s[N];int hash(char ch) {    if (ch == '+' || ch == '-') return 1;    else return 2;}void cal(int num[N], char op[N]) {    int b = num[t1--];    int a = num[t1--];    char theta = op[t2--];    if (theta == '+') num[++t1] = a + b;    else if (theta == '-') num[++t1] = a - b;    else if (theta == '*') num[++t1] = a * b;    else num[++t1] = a / b;}int main() {    scanf("%[^\n]", s);    int ls = strlen(s);    for (int i = 0; i < ls; i++) {        if (s[i] == ' ' || s[i] == '=') continue;        char c = s[i];        if ((c - '0' >= 0) && (c - '0' <= 9)) {            int j = i, t = 0;            while (j < ls && (s[j] - '0' >= 0) && (s[j] - '0' <= 9)) t = 10 * t + s[j++] - '0';            num[++t1] = t;            i = j - 1;        }        else {            while (t2 >= 0 && hash(c) <= hash(op[t2])) cal(num, op);            op[++t2] = c;        }    }    while (t2 >= 0) cal(num, op);    printf("%d", num[0]);    return 0;}